mapper.select:
SELECT s.stock_id AS stockId, s.product_id AS productId, s.warehouse_id AS warehouseId, s.business_id AS businessId, s.total_quantity AS totalQuantity, s.reserved_quantity AS reservedQuantity, s.available_stock AS availableStock, s.updated_at AS updatedAt, s.created_at AS createdAt, s.description AS description, s.is_deleted AS isDeleted FROM stocks s
s.is_deleted = 0
AND s.warehouse_id = #{warehouseId}
AND s.product_id = #{productId}
ORDER BY s.available_stock ASC
ORDER BY s.available_stock DESC
